var synthetics = require('Synthetics');
const AWS = require('aws-sdk');
const log = require('SyntheticsLogger');
const https = require('https');
const http = require('http');

const ssmClient = new AWS.SSM();
const apiCanaryBlueprint = async function () {
    const postData = "";

    const verifyRequest = async function (requestOption) {
      return new Promise((resolve, reject) => {
        log.info("Making request with options: " + JSON.stringify(requestOption));
        let req
        if (requestOption.port === 443) {
          req = https.request(requestOption);
        } else {
          req = http.request(requestOption);
        }
        req.on('response', (res) => {
          log.info(`Status Code: ${res.statusCode}`)
          log.info(`Response Headers: ${JSON.stringify(res.headers)}`)
          //If the response status code is not a 2xx success code
          if (res.statusCode < 200 || res.statusCode > 299) {
             reject("Failed: " + requestOption.path);
          }
          res.on('data', (d) => {
            log.info("Response: " + d);
          });
          res.on('end', () => {
            resolve();
          })
        });

        req.on('error', (error) => {
          reject(error);
        });

        if (postData) {
          req.write(postData);
        }
        req.end();
      });
    }

    const headers = {}
    headers['User-Agent'] = [synthetics.getCanaryUserAgentString(), headers['User-Agent']].join(' ');
    const url = await ssmClient.getParameter({Name: 'APIURL'}).promise();
    const requestOptions = {"hostname":url.Parameter.APIURL.Value.split.split('.com/')[0] + '.com',"method":"GET","path":"/prod/","port":443}
    requestOptions['headers'] = headers;
    await verifyRequest(requestOptions);
};

exports.handler = async () => {
    return await apiCanaryBlueprint();
};
